```typescript
const transformToTaskSuggestion = async (suggestion: StudySuggestion, task: Partial<ExtendedTask>): Promise<TaskSuggestion> => {
  const aiSuggestion: AISuggestion = {
    id: suggestion.id,
    type: mapSuggestionType(suggestion.type),
    title: suggestion.title,
    description: suggestion.description,
    priority: suggestion.priority,
    createdAt: new Date().toISOString(), // Add required field
    confidence: suggestion.confidence,
    reasoning: suggestion.reasoning,
    suggestedTime: suggestion.suggestedTime,
    duration: suggestion.duration,
    subject: suggestion.subject,
    metadata: suggestion.metadata
  };
  return await convertAISuggestionToTaskSuggestion(aiSuggestion, task);
};
