Create src/components/tasks/QuickTaskInput.tsx following established patterns:

```typescript
interface QuickTaskInputProps {
  onTaskCreate: (task: Omit<ExtendedTask, 'id' | 'completed'>) => void;
  placeholder?: string;
  className?: string;
}

export const QuickTaskInput: React.FC<QuickTaskInputProps> = ({
  onTaskCreate,
  placeholder = "Quick task entry...",
  className
}) => {
  const [input, setInput] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    setIsSubmitting(true);
    const task = parseQuickInput(input.trim());
    onTaskCreate(task);
    setInput('');
    setIsSubmitting(false);
  };

  return (
    <form onSubmit={handleSubmit} className={cn("flex gap-2", className)}>
      <Input
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder={placeholder}
        disabled={isSubmitting}
        className="flex-1"
      />
      <Button type="submit" disabled={!input.trim() || isSubmitting}>
        Add
      </Button>
    </form>
  );
};
```

