Enhance AITaskSuggestions.tsx with performance optimizations:

```typescript
// Add suggestion caching
const [suggestionCache, setSuggestionCache] = useState<Map<string, TaskSuggestion[]>>(new Map());
const [loadingStates, setLoadingStates] = useState<Record<string, boolean>>({});

// Enhanced suggestion generation with caching
const generateTaskSuggestions = useCallback(async () => {
  const cacheKey = `${currentTask.title}-${currentTask.type}-${currentTask.priority}`;

  // Check cache first
  if (suggestionCache.has(cacheKey)) {
    setSuggestions(suggestionCache.get(cacheKey) || []);
    return;
  }

  if (!currentTask.title || currentTask.title.trim().length < 3) {
    setSuggestions([]);
    return;
  }

  setLoading(true);
  setError(null);

  try {
    // Enhanced context with user patterns
    const context: SuggestionContext = {
      currentTime: new Date(),
      upcomingTasks: [],
      recentActivity: [],
      userPreferences: MemoSparkAI.getDefaultPreferences(),
      taskContext: currentTask,
      suggestionTypes: ['task_enhancement', 'time_optimization', 'priority_adjustment']
    };

    const result = await MemoSparkAI.generateIntelligentSuggestions([], 'current-user', context);
    const enhancedSuggestions = await Promise.all(
      result.suggestions.slice(0, maxSuggestions).map(s => transformToTaskSuggestion(s, currentTask))
    );

    // Cache results
    suggestionCache.set(cacheKey, enhancedSuggestions);
    setSuggestions(enhancedSuggestions);
  } catch (err) {
    // Enhanced error handling with fallbacks
    console.error('AI suggestions failed:', err);
    setError('Unable to generate suggestions. Using fallback recommendations.');
    setSuggestions(generateFallbackSuggestions(currentTask));
  } finally {
    setLoading(false);
  }
}, [currentTask, maxSuggestions, suggestionCache]);

// Preload suggestions for common patterns
useEffect(() => {
  const preloadTimer = setTimeout(() => {
    if (currentTask.title && currentTask.title.length >= 2) {
      generateTaskSuggestions();
    }
  }, 500); // Reduced debounce for better UX

  return () => clearTimeout(preloadTimer);
}, [generateTaskSuggestions]);
