// Add mobile-specific hooks and utilities
import { useSwipeable } from 'react-swipeable';
import { useMediaQuery } from '@/hooks/useMediaQuery';

// Enhanced ProgressiveTaskCapture with mobile optimizations
const ProgressiveTaskCapture = ({ open, onOpenChange, onTaskCreate }) => {
  const isMobile = useMediaQuery('(max-width: 768px)');
  const [touchStartY, setTouchStartY] = useState(0);

  // Swipe navigation for mobile
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => step < totalSteps && handleNext(),
    onSwipedRight: () => step > 1 && handlePrev(),
    trackMouse: false,
    trackTouch: true,
    delta: 50
  });

  // Enhanced mobile dialog
  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent 
        className={cn(
          "sm:max-w-lg max-h-[90vh] overflow-y-auto",
          isMobile && "fixed inset-x-2 bottom-2 top-auto max-h-[80vh] rounded-t-xl"
        )}
        {...swipeHandlers}
      >
        {/* Mobile-optimized header with larger touch targets */}
        <DialogHeader className={cn("pb-2", isMobile && "pb-4")}>
          <div className="flex items-center justify-between mb-4">
            <div className="flex justify-center space-x-2 flex-1">
              {[...Array(totalSteps)].map((_, index) => (
                <button
                  key={index}
                  onClick={() => setStep(index + 1)}
                  className={cn(
                    "h-2 rounded-full transition-all duration-300 touch-manipulation",
                    isMobile ? "min-w-[44px] h-3" : "w-4", // Larger for mobile
                    index + 1 === step ? "bg-primary" : "bg-primary/20"
                  )}
                  aria-label={`Step ${index + 1}`}
                />
              ))}
            </div>
          </div>
        </DialogHeader>

        {/* Mobile-optimized form fields with larger inputs */}
        <div className={cn("space-y-4", isMobile && "space-y-6")}>
          <Input
            className={cn("h-11", isMobile && "h-14 text-base")}
            // Enhanced mobile input properties
