// Enhanced Stu component with context awareness
export const StuTaskGuidance: React.FC<StuTaskGuidanceProps> = ({
  currentStep,
  taskData,
  userContext = {},
  onGuidanceAction,
  size = 'md',
  position = 'embedded',
  className
}) => {
  const [stuAnimation, setStuAnimation] = useState<StuAnimationState>('idle');
  const [personalizedMessage, setPersonalizedMessage] = useState('');
  const [celebrationActive, setCelebrationActive] = useState(false);

  // Personalized message selection based on user context
  const getPersonalizedMessage = useCallback((step: string) => {
    const context = userContext;
    const isFirstTime = context.totalTasks === 0;
    const isProductive = context.tasksCompletedToday >= 3;

    let messageCategory;
    if (step === 'quickCapture') {
      messageCategory = isFirstTime ? 'firstTime' : isProductive ? 'productive' : 'returning';
    } else if (step === 'aiSuggestions') {
      const avgConfidence = context.averageSuggestionConfidence || 0.5;
      messageCategory = avgConfidence > 0.8 ? 'highConfidence' : 
                       avgConfidence > 0.4 ? 'lowConfidence' : 'noSuggestions';
    }

    const messages = enhancedStuMessages[step]?.[messageCategory] || stuMessages[step];
    return messages[Math.floor(Math.random() * messages.length)];
  }, [userContext]);

  // Celebration effects with confetti
  const triggerCelebration = useCallback(() => {
    import('canvas-confetti').then(confetti => {
      confetti.default({
        particleCount: 100,
        spread: 70,
        origin: { y: 0.6 },
        colors: ['#16a34a', '#22c55e', '#4ade80'] // MemoSpark green variants
      });
    });
    setCelebrationActive(true);
    setStuAnimation('excited');

    setTimeout(() => {
      setCelebrationActive(false);
      setStuAnimation('idle');
    }, 3000);
  }, []);

  // Enhanced animation variants
  const enhancedKoalaVariants = {
    ...koalaVariants,
    celebrating: {
      scale: [1, 1.2, 1.1, 1.2, 1],
      rotate: [0, -5, 5, -3, 0],
      transition: { duration: 1.5, repeat: 2 }
    },
    thinking: {
      rotate: [-2, 2, -2],
      transition: { duration: 0.8, repeat: Number.POSITIVE_INFINITY }
    }
  };

  // Context-aware step handling
  useEffect(() => {
    if (currentStep === 'completed') {
      triggerCelebration();
      const celebrationMessage = getCelebrationMessage(userContext);
      setPersonalizedMessage(celebrationMessage);
    } else {
      const message = getPersonalizedMessage(currentStep);
      setPersonalizedMessage(message);
    }
  }, [currentStep, getPersonalizedMessage, triggerCelebration, userContext]);

